CREATE TABLE user (id BIGINT AUTO_INCREMENT, username VARCHAR(128) NOT NULL UNIQUE, email VARCHAR(128) NOT NULL UNIQUE, salt VARCHAR(128), password VARCHAR(128), name VARCHAR(255), is_active TINYINT(1) DEFAULT '0', is_super_admin TINYINT(1) DEFAULT '0', last_login DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX name_idx_idx (username), PRIMARY KEY(id)) ENGINE = INNODB;
